mapper.select:
SELECT movie_id, title, runtime, releasedate FROM movie
YEAR (releasedate) = #{year}
AND MONTH (releasedate) = #{month}
AND QUARTER (releasedate) = #{quarter}
AND title like CONCAT('%',#{title},'%')
AND movie_id in ( SELECT movie_id FROM movie_actor NATURAL JOIN movie NATURAL JOIN actor WHERE actor_name = #{actor} )
AND movie_id in ( SELECT movie_id FROM movie NATURAL JOIN movie_genres NATURAL JOIN genres WHERE genres_name = #{genres} )
AND movie_id in ( SELECT movie_id FROM movie NATURAL JOIN movie_director NATURAL JOIN director WHERE director_name = #{director} )
AND movie_id in ( SELECT movie_id FROM ( SELECT movie_id, AVG(score) AS avgScore FROM review GROUP BY movie_id HAVING avgScore > #{score} ) AS movieScore NATURAL JOIN movie )
